package flow_control_statement;

import java.util.Scanner;

public class Assignment4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char x = 'a';
		char y = 'e';
		
		if (x < y) {
			System.out.println(x + ", " + y);
		} else {
			System.out.println(y + ", " + x);
		}
	}
}
